async function sendUrl(e,t){return(await fetch(e,{method:"POST",mode:"cors",credentials:"same-origin",headers:{"Content-Type":"application/json"},redirect:"follow",referrerPolicy:"no-referrer",body:JSON.stringify(t)})).json()}function showShortUrl(e){const t=document.querySelector(".short-url-text");t.innerText=`${window.location.href}/url/${e}`,t.href=`/url/${e}`}function bindSendButton(){document.querySelector("button#send-button").addEventListener("click",async e=>{const t=document.querySelector("input.url-input-field");showShortUrl((await sendUrl("/url",{url:t.value})).shortUrlHash),e.preventDefault()})}bindSendButton();